   "source": [
    "library(ggplot2)\n",
    "\n",
    "ggplot(df, aes(x = math.percentage, y = reading.score.percentage)) +\n",
    "    geom_point(color = \"blue\") +                #plots to see where the data are\n",
    "    geom_smooth(method = \"lm\", color = \"red\") + # Linear line for easire analysis\n",
    "    labs(\n",
    "        title = \"Math Percentage vs Reading Score\",     #title\n",
    "        x = \"Math Percentage\",                          #X-Title\n",
    "        y = \"Reading Score Percentage\"                  #Y-Tilte\n",
    "    ) +\n",
    "    theme_minimal()\n"
   ]
